mapper.select:

select

from TXL
where TXL_DELFLAG = 1


		and ZZJG_ID = #{zzjgId,jdbcType=VARCHAR}


		and TXL_NAME = #{name,jdbcType=VARCHAR}

select

from TXL
where ZZJG_ID = #{zzjgId,jdbcType=VARCHAR}


		and TXL_DEPARTMENT = #{bm,jdbcType=VARCHAR}


		and TXL_POSITION = #{zw,jdbcType=VARCHAR}


order by TXL_NAME

select count(*) from TXL where TXL_DELFLAG=1
and TXL_NAME like '%${keywords}%'
and ZZJG_ID = #{orderField,jdbcType=VARCHAR}
