mapper.select:
select M.MSQGL_DETAILED_ID, M.JTJLK_ID, M.MSQGL_DETAILED_MSLB, M.MSQGL_DETAILED_MSSJ, M.MSQGL_DETAILED_MSDD, M.MSQGL_DETAILED_MSG, M.MSQGL_DETAILED_LSZH_ID, M.MSQGL_DETAILED_ADDTIME, M.MSQGL_DETAILED_DELFLAG, M.MSQGL_DETAILED_PJ, M.MSQGL_DETAILED_JL, J.JTJLK_NAME from MSQGL_DETAILED M left join JTJLK J on J.JTJLK_ID = M.JTJLK_ID where M.MSQGL_DETAILED_DELFLAG = 1
and M.JTJLK_ID = #{jlId,jdbcType=VARCHAR}
and M.MSQGL_DETAILED_MSG = #{userName,jdbcType=VARCHAR}
order by M.MSQGL_DETAILED_ADDTIME

select * from (select top ${currentPageSize} * from (select top(${pageNum}*${pageSize}) * from MSQGL_DETAILED T where MSQGL_DETAILED_DELFLAG=1
and MSQGL_DETAILED_ID = #{keywords}
order by T.MSQGL_DETAILED_ID desc ) as t1 order by MSQGL_DETAILED_ID asc) as t2 order by JTJLK_ID desc
